import React, { createContext, useState } from 'react';

export const SelectedContext = createContext();

export const SelectedProvider = ({ children }) => {
  const [selectedItem, setSelectedItem] = useState(null);
  const [selectedTechnology, setSelectedTechnology] = useState(null);
  const [agentsByTechnology, setAgentsByTechnology] = useState({
    'PDF Ai PDF': [],
    'Kraftful product coach': [],
    'Consensus': [],
  });
  const [systemMessage, setSystemMessage] = useState(null);
  const [systemRole, setSystemRole] = useState(null);

  const addAgent = (technology) => {
    const newAgent = `${technology} Agent ${Date.now()}`;
    setAgentsByTechnology(prev => ({
      ...prev,
      [technology]: [...(prev[technology] || []), newAgent]
    }));
  };

  return (
    <SelectedContext.Provider 
      value={{ 
        selectedItem, 
        setSelectedItem, 
        selectedTechnology, 
        setSelectedTechnology, 
        agentsByTechnology, 
        addAgent,
        systemMessage,
        setSystemMessage,
        systemRole,
        setSystemRole
      }}
    >
      {children}
    </SelectedContext.Provider>
  );
};
