// MainContent.js
import React, { useState } from 'react';
import './MainContent.css';

const MainContent = () => {
  const [message, setMessage] = useState('');

  const handleInputChange = (e) => {
    setMessage(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Message sent:', message);
    // You can add functionality here to handle the message (e.g., sending it to a server)
    setMessage(''); // Clear the input after submission
  };

  return (
    <div className="main-content">
      <div className="content-header">
        <h1>ChatGPT</h1>
      </div>
      <div className="content-body">
        <div className="messages">
          {/* Display messages here */}
          <p>Explain superconductors</p>
          <p>Pick outfit to look good on camera</p>
          <p>Write a story in my favorite genre</p>
          <p>Email for plumber quote</p>
        </div>
        <form className="message-input" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Type your message..."
            value={message}
            onChange={handleInputChange}
          />
          <button type="submit">Send</button>
        </form>
      </div>
    </div>
  );
};

export default MainContent;
