import React, { useContext, useState } from 'react';
import { SelectedContext } from './SelectedContext';
import './MainContent.css';

const MainContent = () => {
  const { selectedItem, selectedTechnology, addAgent } = useContext(SelectedContext);
  const [newAgent, setNewAgent] = useState('');

  const handleAddAgent = () => {
    if (selectedTechnology && newAgent) {
      addAgent(selectedTechnology, newAgent);
      setNewAgent(''); // Clear the input field after adding
    }
  };

  return (
    <div className="main-content">
      <div className="content-header">
        <h1>ChatGPT</h1>
      </div>
      <div className="content-body">
        {selectedItem ? (
          <form className="message-input">
            <input
              type="text"
              placeholder={`Type your message for ${selectedItem}...`}
            />
            <button type="submit">Send</button>
          </form>
        ) : (
          <div className="technology-options">
            <h2>Choose a Technology</h2>
            <ul>
              <li>PDF Ai PDF</li>
              <li>Kraftful product coach</li>
              <li>Consensus</li>
            </ul>
            {selectedTechnology && (
              <div className="add-agent">
                <input 
                  type="text" 
                  placeholder="Add a new agent" 
                  value={newAgent} 
                  onChange={(e) => setNewAgent(e.target.value)} 
                />
                <button onClick={handleAddAgent}>Add Agent</button>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default MainContent;
