import React, { useContext } from 'react';
import { SelectedContext } from './SelectedContext';
import './MainContent.css';

const MainContent = () => {
  const { selectedItem, systemMessage, systemRole } = useContext(SelectedContext);

  return (
    <div className="main-content">
      <div className="content-header">
        <h1>ChatGPT</h1>
      </div>
      <div className="content-body">
        {!selectedItem ? (
          <div className="setup-prompt">
            <h2>Please select an agent to proceed.</h2>
          </div>
        ) : (!systemMessage || !systemRole) ? (
          <div className="setup-prompt">
            <h2>Please select a system message and system role to start chatting.</h2>
          </div>
        ) : (
          <form className="message-input">
            <input
              type="text"
              placeholder={`Type your message for ${selectedItem}...`}
            />
            <button type="submit">Send</button>
          </form>
        )}
      </div>
    </div>
  );
};

export default MainContent;
