import React, { useContext, useState } from 'react';
import { SelectedContext } from './SelectedContext';
import './MainContent.css';

const MainContent = () => {
  const { selectedItem, setSelectedItem } = useContext(SelectedContext);
  const [isAddingAgent, setIsAddingAgent] = useState(false);
  const [newAgentName, setNewAgentName] = useState('');

  const handleAddAgent = (e) => {
    e.preventDefault();
    if (newAgentName) {
      // Add logic here to add the new agent
      setSelectedItem(newAgentName); // Automatically select the newly added agent
      setNewAgentName(''); // Clear the input field
      setIsAddingAgent(false); // Hide the "Add Agent" form
    }
  };

  return (
    <div className="main-content">
      <div className="content-header">
        <h1>Explore GPTs</h1>
      </div>
      <div className="content-body">
        {selectedItem ? (
          <div className="selected-agent">
            <div className="agent-header">
              <h2>{selectedItem}</h2>
            </div>
            <div className="messages">
              <p>Start a conversation with {selectedItem}.</p>
            </div>
            <form className="message-input">
              <input
                type="text"
                placeholder={`Type your message for ${selectedItem}...`}
              />
              <button type="submit">Send</button>
            </form>
          </div>
        ) : (
          <div className="no-agent">
            {isAddingAgent ? (
              <form className="add-agent-form" onSubmit={handleAddAgent}>
                <input
                  type="text"
                  placeholder="Enter new agent name..."
                  value={newAgentName}
                  onChange={(e) => setNewAgentName(e.target.value)}
                />
                <button type="submit">Add Agent</button>
                <button type="button" onClick={() => setIsAddingAgent(false)}>
                  Cancel
                </button>
              </form>
            ) : (
              <div className="agent-prompt">
                <p>No agent selected.</p>
                <button onClick={() => setIsAddingAgent(true)}>Add Agent</button>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default MainContent;
