import React, { useContext } from 'react';
import { SelectedContext } from './SelectedContext';
import './Sidebar.css';

const Sidebar = () => {
  const { selectedItem, setSelectedItem, selectedTechnology, setSelectedTechnology } = useContext(SelectedContext);

  const handleSelectTechnology = (technology) => {
    setSelectedTechnology(technology);
    setSelectedItem(null); // Clear selected agent when technology changes
  };

  const handleSelectAgent = (agent) => {
    setSelectedItem(agent);
  };

  // Dummy agents based on technology
  const agentsByTechnology = {
    'PDF': ['PDF Agent 1', 'PDF Agent 2'],
    'AI': ['AI Agent 1', 'AI Agent 2'],
    'PDF Consensus': ['Consensus Agent 1', 'Consensus Agent 2'],
  };

  return (
    <div className="sidebar">
      <h2>Choose a Technology</h2>
      <ul>
        <li 
          className={selectedTechnology === 'PDF' ? 'selected' : ''} 
          onClick={() => handleSelectTechnology('PDF')}
        >
          PDF Ai PDF
        </li>
        <li 
          className={selectedTechnology === 'AI' ? 'selected' : ''} 
          onClick={() => handleSelectTechnology('AI')}
        >
          Kraftful product coach
        </li>
        <li 
          className={selectedTechnology === 'PDF Consensus' ? 'selected' : ''} 
          onClick={() => handleSelectTechnology('PDF Consensus')}
        >
          PDF Consensus
        </li>
      </ul>

      {selectedTechnology && (
        <div className="agents-list">
          <h3>Available Agents</h3>
          <ul>
            {agentsByTechnology[selectedTechnology]?.map(agent => (
              <li 
                key={agent}
                className={selectedItem === agent ? 'selected' : ''}
                onClick={() => handleSelectAgent(agent)}
              >
                {agent}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Sidebar;
