import React, { useContext } from 'react';
import { SelectedContext } from './SelectedContext';
import './Sidebar.css';

const Sidebar = () => {
  const { selectedItem, setSelectedItem, selectedTechnology, setSelectedTechnology, agentsByTechnology, systemMessage, systemRole, setSystemMessage, setSystemRole } = useContext(SelectedContext);

  const handleSelectTechnology = (technology) => {
    setSelectedTechnology(technology);
    setSelectedItem(null); // Clear selected agent when technology changes
  };

  const handleSelectAgent = (agent) => {
    setSelectedItem(agent);
    setSystemMessage(null); // Clear system selections when selecting a new agent
    setSystemRole(null);
  };

  const handleSystemMessageChange = (event) => {
    setSystemMessage(event.target.value);
  };

  const handleSystemRoleChange = (event) => {
    setSystemRole(event.target.value);
  };

  return (
    <div className="sidebar">
      <h2>Choose a Technology</h2>
      <ul>
        <li 
          className={selectedTechnology === 'PDF Ai PDF' ? 'selected' : ''} 
          onClick={() => handleSelectTechnology('PDF Ai PDF')}
        >
          PDF Ai PDF
        </li>
        <li 
          className={selectedTechnology === 'Kraftful product coach' ? 'selected' : ''} 
          onClick={() => handleSelectTechnology('Kraftful product coach')}
        >
          Kraftful product coach
        </li>
        <li 
          className={selectedTechnology === 'Consensus' ? 'selected' : ''} 
          onClick={() => handleSelectTechnology('Consensus')}
        >
          Consensus
        </li>
      </ul>

      {selectedTechnology && !selectedItem && (
        <button onClick={() => handleSelectTechnology(selectedTechnology)}>Add Agent</button>
      )}

      {selectedItem && (
        <div className="system-settings">
          <h3>Select System Message</h3>
          <select value={systemMessage || ''} onChange={handleSystemMessageChange}>
            <option value="">Select a message...</option>
            <option value="System Message 1">System Message 1</option>
            <option value="System Message 2">System Message 2</option>
          </select>

          <h3>Select System Role</h3>
          <select value={systemRole || ''} onChange={handleSystemRoleChange}>
            <option value="">Select a role...</option>
            <option value="Admin">Admin</option>
            <option value="User">User</option>
          </select>
        </div>
      )}

      {selectedTechnology && (
        <div className="agents-list">
          <h3>Available Agents</h3>
          <ul>
            {agentsByTechnology[selectedTechnology]?.map(agent => (
              <li 
                key={agent}
                className={selectedItem === agent ? 'selected' : ''}
                onClick={() => handleSelectAgent(agent)}
              >
                {agent}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Sidebar;
