// Sidebar.js
import React, { useContext } from 'react';
import { SelectedContext } from './SelectedContext';
import './Sidebar.css';

const Sidebar = () => {
  const { selectedItem, setSelectedItem } = useContext(SelectedContext);

  const handleSelect = (item) => {
    setSelectedItem(item);
  };

  return (
    <div className="sidebar">
      <h2>Menu</h2>
      <ul>
        <li 
          className={selectedItem === 'Item 1' ? 'selected' : ''} 
          onClick={() => handleSelect('Item 1')}
        >
          Item 1
        </li>
        <li 
          className={selectedItem === 'Item 2' ? 'selected' : ''} 
          onClick={() => handleSelect('Item 2')}
        >
          Item 2
        </li>
        <li 
          className={selectedItem === 'Item 3' ? 'selected' : ''} 
          onClick={() => handleSelect('Item 3')}
        >
          Item 3
        </li>
        <li 
          className={selectedItem === 'Item 4' ? 'selected' : ''} 
          onClick={() => handleSelect('Item 4')}
        >
          Item 4
        </li>
      </ul>
    </div>
  );
};

export default Sidebar;
